SELECT n.id, familia, name, otch, dater, pol, adres_id, pa_id, ma_id, kurs  FROM Население n  LEFT JOIN БазаДомов d ON d.date_fiks=n.date_fiks and d.id=n.adres_id  WHERE n.date_fiks='1899-12-30' and n.propis=true and n.candelete=false  and  GetVozrast('2019-08-15',n.dater)<=17 and d.punkt=3 and n.present=true and n.datep>='2010-08-15' and n.datep<='2019-08-15' ORDER BY familia,name,otch 